import { Component, OnInit } from '@angular/core';
import { FormArray, FormControl, FormGroup, Validators } from '@angular/forms';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit{
  genders = ['male', 'female'];
  signupForm: FormGroup;
  forbiddenUsernames = ['Chris', 'Anna'];

  ngOnInit(){
    this.signupForm = new FormGroup({ // creates a form
      // now adding form controls - key/value pairs
      'gender': new FormControl('female'), // can set a default rather than choosing null

      // creating a form group within the main form group
      'userData': new FormGroup({                                                   // need to bind this for it to work because the validator gets called by Angular rather than inside the typescript file
        'username': new FormControl(null, [Validators.required, this.forbiddenNames.bind(this)]), // form control is done here rather than in the html file by using Validators
        'email': new FormControl(null, [Validators.required, Validators.email, this.forbiddenEmails]), // if multiple validators use an array
      }),

      // form array - holds an array of controls
      'hobbies': new FormArray([])
    }); 
  }

  onSubmit(){
    console.log(this.signupForm);
  }

  onAddHobby(){ // this method creates a new form control with no default input (null) and pushes it onto our hobbies Form Array
    const control = new FormControl(null, Validators.required);
    (<FormArray>this.signupForm.get('hobbies')).push(control); // have to cast to form array
  }

  getControls(){
    return (<FormArray>this.signupForm.get('hobbies')).controls;
  }

  // creating our own validator - takes in a form control and returns an object with a key name that has a value of either true or false
  forbiddenNames(control: FormControl): {[s: string] : boolean} 
  {
    if(this.forbiddenUsernames.indexOf(control.value) !== -1){ // if the input from the formControl matches any of the values in our forbiddenUsernames array
      return {'nameisForbidden': true};
    } // otherwise indexOf returns -1 if it doesn't find the string in the array
    else{ // if validation is sucessfull you need to return nothing or null!
      return null
    }
  }

  // creating a custom asynchronous validator
  forbiddenEmails(control: FormControl): Promise<any> | Observable<any>{
    const promise = new Promise<any>((resolve, reject) => {
      setTimeout(() => {
        if(control.value === 'test@test.com'){
          resolve({'emailIsForbidden': true});
        }
        else{
          resolve(null);
        }
      }, 1500)
    });

    return promise;
  }
}
<div class="container">
  <div class="row">
    <div class="col-xs-12 col-sm-10 col-md-8 col-sm-offset-1 col-md-offset-2">
      <!-- tells Angular to take our created form rather than create one automatically -->
      <form [formGroup]="signupForm" (ngSubmit)="onSubmit()">
        <div formGroupName="userData"> <!-- grouping username and email inputs together into a 'userData' FormGroup within the main signupForm FormGroup -->
          <div class="form-group">
            <label for="username">Username</label>
            <!-- linking each input to the controls in our form in the ts file using formControlName-->
            <input
              type="text"
              id="username"
              formControlName="username"
              class="form-control">
              <!-- the spans will only show if person has clicked the field but has given invalid input -->
              <!-- need to update the paths for get since this input is now nested-->
            <span
            *ngIf="!signupForm.get('userData.username').valid && signupForm.get('userData.username').touched"
            class="help-block">
              <!-- nesting some spans within the larger one since there are multiple different types of errors that could occur here now -->
              <span *ngIf="signupForm.get('userData.username').errors['nameisForbidden']">
                This name is invalid!
              </span>
              <span *ngIf="signupForm.get('userData.username').errors['required']">
                This field is required!
              </span>
            </span>
          </div>
          <div class="form-group">
            <label for="email">email</label>
            <input
              type="text"
              id="email"
              formControlName="email"
              class="form-control">
              <!-- need to update the paths for get since this input is now nested-->
            <span
            *ngIf="!signupForm.get('userData.email').valid && signupForm.get('userData.email').touched"
            class="help-block">Please enter a valid email!
            </span>
          </div>
        </div>
        <div class="radio" *ngFor="let gender of genders">
          <label>
            <input
              type="radio"
              formControlName="gender"
              [value]="gender">{{ gender }}
          </label>
        </div>
        <div formArrayName="hobbies"> <!-- linking our FormArray -->
          <h4>Your Hobbies</h4>
          <button class="btn btn-default" type="button" (click)="onAddHobby()">Add Hobby</button>
          <!-- loops through our hobbies FormArray from the ts file while also grabbing the index and 
            prints each new form control to the screen and giving it a name that corresponds to the index -->
            <!-- so with each form control we add to the FormsArray using the onAddHobby method we can then
              output it to the screen and fill it with an individual hobby when we run this ngFor and use getControls-->
          <div class="form-group" *ngFor="let hobbyControl of getControls(); let i = index">
            <input type="text" class="form-control" [formControlName]="i">
          </div>
        </div>
        <span
          *ngIf="!signupForm.valid && signupForm.touched"
          class="help-block">Please enter valid data!
        </span>
        <button class="btn btn-primary" type="submit">Submit</button>
      </form>
    </div>
  </div>
</div>
